mapper.select:

SELECT @rownum := @rownum + 1 rownumber, bno, title, content, writer, regDate, updateDate
FROM tbl_board,(SELECT @rownum := 0) rownum
limit #{amount} offset #{pageNum}

mapper.update:

UPDATE tbl_board
SET title = #{title}, content = #{content}, writer = #{writer}, updateDate = current_timestamp
WHERE bno = #{bno}
